    def get_category(self):  # using the designer I created 3 filter widgets, this function change widget
        """
        :return: change the filter widget according to category
        """
        if self.categories.currentText() == 'Laptop':
            self.filter_phone_widget.hide()
            self.filter_tablet_widget.hide()
            self.filter_laptop_widget.show()
        if self.categories.currentText() == 'Tablet':
            self.filter_phone_widget.hide()
            self.filter_laptop_widget.hide()
            self.filter_tablet_widget.show()
        if self.categories.currentText() == 'Smartphone':
            self.filter_tablet_widget.hide()
            self.filter_laptop_widget.hide()
            self.filter_phone_widget.show()

    def user_functions(self):
        # if there is no user it opens the log_in
        if not self.user:
            self.user = lw.log_in_window()
        # if there is a user it opens the account widget with some information
        else:
            self.Form = QtWidgets.QWidget()
            acc = account.Ui_Form()
            acc.setupUi(self.Form, self.user)
            self.Form.show()

    def display_products(self, products):
        self.button_list = []
        self.label_list = []
        # creates a scroll area on the main page
        self.scroll = QtWidgets.QScrollArea(self.centralwidget)
        self.scroll.setGeometry(QtCore.QRect(290, 100, 960, 500))
        font = QtGui.QFont()
        font.setFamily("Times New Roman")
        font.setPointSize(10)
        if not products:
            label = QtWidgets.QLabel(self.scroll)
            label.setFont(font)
            label.setGeometry(QtCore.QRect(20, 10, 770, 30))
            label.setText('No Such Products Yet')
        # for each product creates a row with most important info and view_more function
        else:
            form_layout = QtWidgets.QFormLayout()
            for i in range(len(products)):
                # self.product.setFont(font)
                if products[i].category == 1:
                    type = 'Laptop'
                if products[i].category == 2:
                    type = 'Tablet'
                if products[i].category == 3:
                    type = 'Smartphone'
                self.label_list.append(QtWidgets.QLabel())
                self.label_list[i].setText(f'Category: {type};  Name: {products[i].name};  Brand: {products[i].brand};  Processor: {products[i].processor};  Price: {products[i].price}{products[i].currency}')
                self.button_list.append(QtWidgets.QPushButton())
                self.label_list[i].setFont(font)
                self.button_list[i].setText('View More')
                self.button_list[i].setGeometry(QtCore.QRect(830, 10, 100, 30))
                self.label_list[i].setGeometry(QtCore.QRect(20, 10, 770, 30))
                self.button_list[i].setStyleSheet("background-color: rgb(208, 219, 189);")
                # connect each button to view more to see all info about the product
                self.button_list[i].clicked.connect(lambda ch, i=i: self.view_more(products, i))
                self.button_list[i].setFont(font)
                form_layout.addRow(self.label_list[i], self.button_list[i])
                form_layout.setHorizontalSpacing(10)
                form_layout.setVerticalSpacing(10)
                form_layout.addRow(QtWidgets.QLabel('__________________________________________________________________________________________________________________________________'))
            group_box = QtWidgets.QGroupBox()
            group_box.setLayout(form_layout)
            self.scroll.setWidget(group_box)
            self.scroll.setWidgetResizable(True)
        self.scroll.show()

    def search_product(self):
        # gets the data from search inout box and search products with similar names
        found_products = df.search_product(self.search.text())
        if found_products:
            self.display_products(found_products)
        else:
            sg.popup_error('No Products Found', title='ERROR', font=('Bahnschrift', 16), line_width=150)

    def get_filters(self):
        """
        :return: dictionary with user choices
        """
        user_choices = {}
        for processor in self.radio_processor:
            if processor.isChecked():
                user_choices['processor'] = processor.text()
                # this block of code unchecks the choice, not to have problems later
                processor.setAutoExclusive(False)
                processor.setChecked(False)
                processor.setAutoExclusive(True)
        if self.categories.currentText() == 'Laptop':
            user_choices['ram'] = self.ram.currentText()
            for type in self.storage_type:
                if type.isChecked() and type.text() == 'SSD':
                    user_choices['storage_type'] = 1
                if type.isChecked() and type.text() == 'HDD':
                    user_choices['storage_type'] = 0
                type.setAutoExclusive(False)
                type.setChecked(False)
                type.setAutoExclusive(True)
            for graphics in self.graphics:
                if graphics.isChecked() and graphics.text() == 'Discrete':
                    user_choices['graphics'] = 1
                if graphics.isChecked() and graphics.text() == 'Integrated':
                    user_choices['graphics'] = 0
                graphics.setAutoExclusive(False)
                graphics.setChecked(False)
                graphics.setAutoExclusive(True)
        if self.categories.currentText() == 'Tablet':
            user_choices['ram'] = self.ram_tablet.currentText()
            user_choices['brand'] = self.tab_brand.currentText()
        if self.categories.currentText() == 'Smartphone':
            user_choices['ram'] = self.ram_smartphone.currentText()
            user_choices['brand'] = self.phone_brand.currentText()
        for storage in self.storage_size:
            if storage.isChecked():
                user_choices['storage_size'] = storage.text()
                storage.setAutoExclusive(False)
                storage.setChecked(False)
                storage.setAutoExclusive(True)
        for size in self.screen_size:
            if size.isChecked():
                user_choices['screen_size'] = size.text()
                size.setAutoExclusive(False)
                size.setChecked(False)
                size.setAutoExclusive(True)
        for quality in self.screen_quality:
            if quality.isChecked():
                user_choices['screen_quality'] = quality.text()
                quality.setAutoExclusive(False)
                quality.setChecked(False)
                quality.setAutoExclusive(True)
        if self.categories.currentText() == 'Laptop':
            self.products = df.filter_laptop(user_choices)
        if self.categories.currentText() == 'Tablet':
            self.products = df.filter_tablet(user_choices)
        if self.categories.currentText() == 'Smartphone':
            self.products = df.filter_smartphone(user_choices)
        self.display_products(self.products)

    def view_more(self, product, i):
        # generates a widget with all the information about the product
        self.Form = QtWidgets.QWidget()
        view = view_item.Ui_Form()
        view.setupUi(self.Form, product[i])
        # gives functionality to add to basket and see reviews
        view.basket_button.clicked.connect(lambda: self.add_product_to_basket(product[i]))
        view.reviews_button.clicked.connect(lambda: self.read_reviews(product[i]))
        self.Form.show()

    def read_reviews(self, product):
        # gets all the reviews of a certain product and displays them
        # also you could add more reviews
        reviews = df.see_reviews(product._id)
        self.Form_review = QtWidgets.QWidget()
        view = review.Ui_Form()
        view.setupUi(self.Form_review, product.name, reviews)
        view.pushButton.clicked.connect(lambda: self.add_review(product._id, view.grade.value(), view.textBrowser.toPlainText()))
        self.Form_review.show()

    def add_review(self,product_id, grade, review):
        if len(review) > 4000:
            sg.popup_error('Review to long', title='ERROR', font=('Bahnschrift', 16), line_width=150)
        elif len(review) == 0:
            sg.popup_error('Review to short', title='ERROR', font=('Bahnschrift', 16), line_width=150)
        else:
            # add review to database
            df.add_review(product_id, grade, review)
            # closes the widget
            self.Form_review.deleteLater()

    def add_product_to_basket(self, product):
        if self.user:
            df.add_to_basket(self.user.id, product._id, product.category)
            sg.popup_ok('Added', title='Successful', font=('Bahnschrift', 16), line_width=150)
        else:
            sg.popup_error('No User Found', title='ERROR', font=('Bahnschrift', 16), line_width=150)

